        this.zone.run(() => {
          this.breakpoint.emit({ event, slick, breakpoint });
        });
      });

      this.$instance.on('destroy', (event, slick) => {
        this.zone.run(() => {
          this.destroy.emit({ event, slick });
          this.initialized = false;
        });
      });
